package ch04.java;

public class Logical {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		boolean a = true,b = true;
		System.out.println("a = "+a+", b = "+b);
		System.out.println("a&b "+(a&b));
		System.out.println("a&&b "+(a&&b) );
		System.out.println("a| b "+(a|b));
		System.out.println("a || b "+(a||b));
		System.out.println("a^b "+(a^b));
	}

}
