package ch04.java;

public class Shift {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i =2;
		System.out.println("2 >> 1: "+(i >> 1));
		System.out.println("2 << 1: "+(i << 1));
		System.out.println("2 >>> 1: "+(i >>> 1));
		
		i = -2;
		System.out.println("-2 >> 1: "+(i >> 1));
		System.out.println("-2 << 1: "+(i << 1));
		System.out.println("-2 >>> 1: "+(i >>> 1));
	}

}
