package ch04.java;

public class comparison {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=4,j=5;
		System.out.println("when i is "+i+",j is "+j);
		System.out.println("i<j "+(i<j));
		System.out.println("i>j "+(i>j));
		System.out.println("i<=j "+(i<=j));
		System.out.println("i>=j "+(i>=j));
		System.out.println("i==j "+(i==j));
		System.out.println("i!=j "+(i!=j));
	}

}
