
public class DoubleValue {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		double d;
		d = 3.4;
		System.out.println("跑计d 氦寒e: "+d);
		
		d = 3.0;
		System.out.println("跑计d氦寒e: "+d);
		
		d = 0.1234;
		System.out.println("跑计d氦寒e: "+d);
		
		d = 3;
		System.out.println("跑计d氦寒e:"+d);
		
		d = .1234;
		System.out.println("跑计d氦寒e:"+d);
		
		d = 2.0E-3;
		System.out.println("跑计d氦寒e:"+d);
		
		d = 6.022_140_78E23;
		System.out.println("跑计d氦寒e:"+d);
	}

}
