
public class IntegerValue {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("10i 1357 = "+1357);
		
		int i = 0b10011001; //int O,2i
		System.out.println("2i  0b10011001 = "+i);
		
		long l = 0xadef; //long O,16i
		System.out.println("16 i 0xadef = "+l);
		
		short s = 01357; //short O,8i
		System.out.println("8i 01357" +s);
		
		int a = 0b1100_0110_0011_1010;  //┏urじ
		System.out.println("跑计a = " +a);
		
		long L = 2_135_482_789L; //ノL夹ボ long 俱计
		System.out.println("跑计 L = "+L);
		
		short h = -13666; //t计
		System.out.println("跑计 h = "+h);
		
	}

}
