
public class charvaliue {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char ch;
		ch = 'b';//指定為單一英文字母
		System.out.println("變數ch 的內容為:"+ch);
		
		ch = '中';//指定為單一中文字
		System.out.println("變數ch 的內容為:"+ch);
		
		ch = 98; //指定為變數
		System.out.println("變數ch 的內容為:"+ch);
		//此時這個數值為標準萬國碼的編碼
		
	}

}
